mapper.insert:
INSERT INTO RECORDS (TRANSACTION_CODE, MONEY, "TIMESTAMP", BALANCE, TYPE_OF_TRANSACTION, AC_NUM, ACCOUNT_U) VALUES ( RECORDS_SEQ.NEXTVAL, #{money}, SYSDATE,
(SELECT ACCOUNT_BALANCE FROM ACCOUNTS WHERE AC_NUM = #{account_u})+#{money}, #{type_of_transaction}, #{account_u}, #{ac_num}
(SELECT ACCOUNT_BALANCE FROM ACCOUNTS WHERE AC_NUM = #{ac_num})-#{money}, #{type_of_transaction}, #{ac_num}, #{account_u}
)

mapper.update:

UPDATE ACCOUNTS SET 

	ACCOUNT_BALANCE = ACCOUNT_BALANCE+#{money}
	WHERE AC_NUM = #{account_u}



	ACCOUNT_BALANCE = ACCOUNT_BALANCE-#{money}
	WHERE AC_NUM = #{ac_num}
